    <style>
        body { font-family: 'Space Grotesk', sans-serif; }
        .mono { font-family: 'IBM Plex Mono', monospace; }

        :root {
            --bg: #f7f7f5;
            --bg-secondary: #ffffff;
            --text: #1a1a1a;
            --text-muted: #6b6b6b;
            --border: rgba(0,0,0,0.06);
            --border-strong: rgba(0,0,0,0.1);
        }

        .dark {
            --bg: #161616;
            --bg-secondary: #1e1e1e;
            --text: #ebebeb;
            --text-muted: #8a8a8a;
            --border: rgba(255,255,255,0.06);
            --border-strong: rgba(255,255,255,0.1);
        }

        body {
            background: var(--bg);
            color: var(--text);
            transition: background 0.3s, color 0.3s;
        }

        a:hover { opacity: 0.6; }

        .line { height: 1px; background: var(--border-strong); }

        .video-thumb { position: relative; }

        .play-btn {
            width: 52px;
            height: 52px;
            background: var(--bg-secondary);
            border: 1px solid var(--border);
            border-radius: 50%;
            display: flex;
            align-items: center;
            justify-content: center;
            position: absolute;
            top: 50%;
            left: 50%;
            transform: translate(-50%, -50%);
            backdrop-filter: blur(8px);
        }

        .play-btn svg {
            width: 18px;
            height: 18px;
            fill: var(--text);
            margin-left: 3px;
        }

        .search-box {
            background: var(--bg-secondary);
            border: 1px solid var(--border);
            color: var(--text);
            outline: none;
            transition: border-color 0.2s;
        }

        .search-box::placeholder { color: var(--text-muted); }
        .search-box:focus { border-color: var(--border-strong); }

        .card {
            background: var(--bg-secondary);
            border: 1px solid var(--border);
            transition: border-color 0.2s;
        }

        .card:hover { border-color: var(--border-strong); }

        .img-container {
            background: var(--border);
            overflow: hidden;
        }

        .img-container img {
            opacity: 0;
            transition: opacity 0.4s;
        }

        .img-container img.loaded { opacity: 1; }

        .duration {
            background: var(--bg-secondary);
            border: 1px solid var(--border);
            backdrop-filter: blur(8px);
        }

        .copy-btn {
            opacity: 0;
            transition: opacity 0.15s;
        }

        .link-item:hover .copy-btn { opacity: 1; }

        .modal {
            display: none;
            position: fixed;
            inset: 0;
            z-index: 100;
        }

        .modal.open { display: flex; }

        .modal-backdrop {
            position: absolute;
            inset: 0;
            background: rgba(0,0,0,0.8);
        }

        .modal-content {
            position: relative;
            z-index: 10;
            width: 100%;
            max-width: 900px;
            margin: auto;
            background: var(--bg-secondary);
            border-radius: 16px;
            overflow: hidden;
        }

        .appreciate-modal-content { max-width: 360px; }

        .toast {
            position: fixed;
            bottom: 24px;
            left: 50%;
            transform: translateX(-50%) translateY(100px);
            background: var(--text);
            color: var(--bg);
            padding: 12px 20px;
            font-size: 14px;
            border-radius: 10px;
            opacity: 0;
            transition: all 0.3s ease;
            z-index: 200;
        }

        .toast.show {
            transform: translateX(-50%) translateY(0);
            opacity: 1;
        }

        .scroll-top {
            position: fixed;
            bottom: 24px;
            right: 24px;
            width: 44px;
            height: 44px;
            background: var(--bg-secondary);
            border: 1px solid var(--border);
            color: var(--text);
            border-radius: 12px;
            display: flex;
            align-items: center;
            justify-content: center;
            opacity: 0;
            pointer-events: none;
            transition: all 0.2s;
            cursor: pointer;
        }

        .scroll-top.visible {
            opacity: 1;
            pointer-events: auto;
        }

        .scroll-top:hover { border-color: var(--border-strong); }

        .filter-btn { transition: all 0.15s; }

        .filter-btn.active {
            background: var(--text);
            color: var(--bg);
        }

        .btn-primary {
            background: var(--text);
            color: var(--bg);
            transition: opacity 0.15s;
        }

        .btn-primary:hover { opacity: 0.8; }

        .btn-secondary {
            background: var(--bg-secondary);
            border: 1px solid var(--border);
            color: var(--text);
            transition: border-color 0.15s;
        }

        .btn-secondary:hover { border-color: var(--border-strong); }

        .amount-option {
            background: var(--bg);
            border: 1px solid var(--border);
            color: var(--text);
            transition: all 0.15s;
            cursor: pointer;
        }

        .amount-option:hover { border-color: var(--border-strong); }

        .amount-option.selected {
            background: var(--text);
            color: var(--bg);
            border-color: var(--text);
        }

        .custom-amount {
            background: var(--bg);
            border: 1px solid var(--border);
            color: var(--text);
            outline: none;
        }

        .custom-amount:focus { border-color: var(--border-strong); }
        .custom-amount::placeholder { color: var(--text-muted); }

        /* Article page */
        .article-content {
            line-height: 1.8;
        }

        .article-content p {
            margin-bottom: 1.5em;
        }

        .article-content h2 {
            font-size: 1.5rem;
            font-weight: 600;
            margin: 2em 0 1em;
        }

        .article-content h3 {
            font-size: 1.25rem;
            font-weight: 600;
            margin: 1.5em 0 0.75em;
        }

        .article-content ul, .article-content ol {
            margin: 1em 0;
            padding-left: 1.5em;
        }

        .article-content li {
            margin: 0.5em 0;
        }

        .article-content blockquote {
            border-left: 3px solid var(--border-strong);
            padding-left: 1em;
            margin: 1.5em 0;
            color: var(--text-muted);
            font-style: italic;
        }

        .article-content code {
            font-family: 'IBM Plex Mono', monospace;
            background: var(--bg);
            padding: 0.2em 0.4em;
            border-radius: 4px;
            font-size: 0.9em;
        }

        .article-content pre {
            background: var(--bg);
            padding: 1em;
            border-radius: 8px;
            overflow-x: auto;
            margin: 1.5em 0;
        }

        .article-content pre code {
            background: none;
            padding: 0;
        }

        /* Skeleton loading */
        .skeleton {
            background: linear-gradient(90deg, var(--border) 25%, var(--bg-secondary) 50%, var(--border) 75%);
            background-size: 200% 100%;
            animation: shimmer 1.5s infinite;
            border-radius: 8px;
        }

        @keyframes shimmer {
            0% { background-position: 200% 0; }
            100% { background-position: -200% 0; }
        }
    </style>
</head>

